import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

import MainHeader from "../components/Header/MainHeader";
import FollowingsSection from "../components/Followings/FollowingsSection";
import TwitsSection from "../components/Twits/TwitsSection";
import KeySection from "../components/Keys/KeySection";
import Notification from "../components/Notification/Notification";
import { fetchKeysData } from "../store/keys-actions";
import { fetchAccountsData } from "../store/accounts-actions";
import { keysAction } from "../store/keys";

const MainPage = () => {
  const dispatch = useDispatch();
  const keyList = useSelector((state) => state.keyList);
  const accountsList = useSelector((state) => state.accountsList);
  const notification = useSelector((state) => state.notification.notification);
  const showNotification = useSelector(
    (state) => state.notification.showNotification
  );
  // *** KEYS ***
  useEffect(() => {
    const newKeys = dispatch(fetchKeysData());
    if (newKeys === keyList.items) {
      return;
    } else {
      dispatch(
        keysAction.replaceKeys({
          items: keyList || [],
          quantity: keyList.length,
        })
      );
    }
  }, [dispatch, keyList]);

  // useEffect(() => {
  //   if (isInitial) {
  //     isInitial = false;
  //     return;
  //   }
  //   if (keyList) {
  //     dispatch(fetchKeysData());
  //   }
  // }, [keyList, dispatch]);

  // ////////////////////////////////////////
  // бесконечно выполняется запрос фетчКиз/АккаунтсДата
  // почему так происходит? как избавиться?

  // если убрать юзэффекты отсюда, пропадет отображение ключей / аккаунтов

  // // *** ACCOUNTS ***
  // useEffect(() => {
  //   dispatch(fetchAccountsData());
  //   // console.log("accounts");
  // }, [dispatch, accountsList]);

  return (
    <>
      <Notification
        show={showNotification}
        status={notification.status}
        message={notification.message}
      />
      <MainHeader />
      <main>
        <FollowingsSection />
        <TwitsSection />
        <KeySection />
      </main>
    </>
  );
};

export default MainPage;
