import React, { useRef } from "react";

import classes from "./KeyInput.module.css";
import { addKey, fetchKeysData } from "../../store/keys-actions";
import { useDispatch } from "react-redux";
import { keysAction } from "../../store/keys";

const KeyInput = () => {
  const dispatch = useDispatch();
  const inputEl = useRef(null);

  const reset = () => {
    inputEl.current.value = "";
    inputEl.current.blur();
  };

  const addKeyHandler = (event) => {
    event.preventDefault();

    const inputValue = inputEl.current.value;
    const inputKey = inputValue.split(" ").join("");

    if (inputKey.trim() !== "") {
      addKey(inputKey);
      const newKeys = fetchKeysData();
      dispatch(
        keysAction.replaceKeys({
          items: keyList || [],
          quantity: keyList.length,
        })
      );
    }

    reset();
  };

  return (
    <form onSubmit={addKeyHandler} className={classes.add__keys}>
      <input ref={inputEl} type="text" placeholder="Add keys" />
    </form>
  );
};

export default KeyInput;
