import { createSlice } from "@reduxjs/toolkit";

const keysSlice = createSlice({
  name: "keys",
  initialState: {
    keyList: [],
    changed: false,
  },
  reducers: {
    replaceKeys(state, action) {
      state.keyList = action.payload.keyList;
    },

    addKey(state, action) {
      const newKey = action.payload;
      state.keyList = [
        ...state.keyList,
        { id: newKey.id, content: newKey.content },
      ];
      state.changed = true;
    },

    removeKey(state, action) {
      const id = action.payload;
      state.keyList = state.keyList.filter((key) => key.id !== id);
      state.changed = false;
    },
  },
});

export const keysAction = keysSlice.actions;

export default keysSlice.reducer;
