import { keysAction } from "./keys";
import { notificationActions } from "./notification";

export const sendKeysData = (keyList) => {
  return async (dispatch) => {
    dispatch(
      notificationActions.showNotification({
        status: "pending",
        title: "Sending...",
        message: "Sending cart data!",
      })
    );
    const sendRequest = async () => {
      const response = await fetch(
        "https://keysee-default-rtdb.firebaseio.com/keys.json",
        {
          method: "PUT",
          body: JSON.stringify({
            quantity: keyList.quantity,
            items: keyList.items,
          }),
        }
      );

      if (!response.ok) {
        throw new Error("Something went wrong ...");
      }
    };

    try {
      await sendRequest();
      dispatch(
        notificationActions.showNotification({
          status: "success",
          title: "Success!",
          message: "Sent cart data successfully!",
        })
      );
      setTimeout(() => {
        dispatch(notificationActions.hideNotification());
      }, 2000);
    } catch (error) {
      dispatch(
        notificationActions.showNotification({
          status: "error",
          title: "Error!",
          message: "Sending cart data failed!",
        })
      );
    }
  };
};

export const fetchKeysData = () => {
  return async (dispatch) => {
    const fetchData = async () => {
      const response = await fetch(
        "https://keysee-default-rtdb.firebaseio.com/keys.jsn"
      );

      if (!response.ok) {
        throw new Error("Could not fetch keys data.");
      }

      const data = await response.json();

      return data;
    };

    try {
      const keyListData = await fetchData();

      dispatch(
        keysAction.replaceKeyList({
          items: keyListData.items || [],
          quantity: keyListData.quantity,
        })
      );
    } catch (error) {
      dispatch(
        notificationActions.showNotification({
          status: "error",
          title: "Error!",
          message: "Fetching data failed!",
        })
      );
    }
  };
};
