import { keysAction } from "./keys";
import { notificationActions } from "./notification";

export const fetchKeysData = () => {
  // return async (dispatch) => {
  const fetchData = async () => {
    const response = await fetch("/api/v1/keys", {
      method: "GET",
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
        "Content-Type": "application/json",
      },
    });

    const data = await response.json();
    return data;
  };

  try {
    const keyList = fetchData();
    dispatch(
      keysAction.replaceKeys({
        items: keyList || [],
        quantity: keyList.length,
      })
    );
  } catch (error) {
    console.log(error.message);
    // dispatch(
    //   notificationActions.showNotification({
    //     status: "error",
    //     message: "Fetching keys failed!",
    //   })
    // );
    // setTimeout(() => {
    //   dispatch(notificationActions.hideNotification());
    // }, 1500);
  }
  // };
};

export const addKey = async function (keyItem) {
  const sendRequest = async function () {
    const response = await fetch("/api/v1/keys", {
      method: "POST",
      body: JSON.stringify({
        key: keyItem,
      }),
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
        "Content-Type": "application/json",
      },
    });

    if (!response.ok) {
      throw new Error("Something went wrong ...");
    }

    // fetchKeysData();
  };

  try {
    await sendRequest();
    fetchKeysData();
  } catch (err) {
    console.log(err.message);
  }
};

export const deleteKey = async function (id) {
  const sendRequest = async function () {
    const response = await fetch("/api/v1/keys", {
      method: "DELETE",
      body: JSON.stringify({
        id: id,
      }),
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
        "Content-Type": "application/json",
      },
    });

    if (!response.ok) {
      throw new Error("Something went wrong ...");
    }

    fetchKeysData();
  };

  try {
    await sendRequest();
  } catch (err) {
    // диспатчить ошибку из стейта
    console.log(err.message);
  }
};
