import { createSlice } from "@reduxjs/toolkit";

const keysSlice = createSlice({
  name: "keys",
  initialState: {
    keys: [],
    quantity: 0,
  },
  reducers: {
    replaceKeys(state, action) {
      state.keys = action.payload.keys;
      state.quantity = action.payload.keys.length;
    },

    addKey(state, action) {
      const newKey = action.payload;
      state.keys = [{ id: newKey.id, content: newKey.content }, ...state.keys];
      state.quantity++;
    },

    removeKey(state, action) {
      const id = action.payload;
      state.keys = state.keys.filter((key) => key.id !== id);
      state.quantity--;
    },
  },
});

export const keysAction = keysSlice.actions;

export default keysSlice.reducer;
