import { createSlice } from "@reduxjs/toolkit";

const accountsInitialState = {
  accountsArray: [],
  quantity: 0,
  changed: false,
};

const accountsSlice = createSlice({
  name: "accountsList",
  initialState: accountsInitialState,
  reducers: {
    replaceAccounts(state, action) {
      state.accountsArray = action.payload.accountsArray;
      state.quantity = action.payload.quantity;
    },
    addAccount(state, action) {
      const newAccount = action.payload;
      state.accountsArray.push({
        id: newAccount.id,
        handle: newAccount.handle,
        accUrl: `https://twitter.com/${newAccount.handle}`,
      });
      state.quantity++;
      state.changed = true;

      console.log(state.accountsArray);
    },
    removeAccount(state, action) {
      const id = action.payload;
      state.accountsArray.filter((account) => account.id !== id);
      state.quantity--;
      state.changed = true;
    },
  },
});

export const accountsActions = accountsSlice.actions;

export default accountsSlice.reducer;
