import React, { useRef } from "react";
import { useDispatch } from "react-redux";
import keys, { keysAction } from "../../store/keys";

import classes from "./KeyInput.module.css";

const KeyInput = () => {
  const dispatch = useDispatch();
  const keyInput = useRef();

  const addKeyHandler = () => {
    const keyContent = keyInput.current.focus();
    const keyId = Math.trunc(Math.random() * 1000);
    dispatch(keysAction.addKey({ id: keyId, content: keyContent }));
  };

  return (
    <div className={classes.add__keys}>
      <input
        onSubmit={addKeyHandler}
        type="text"
        placeholder="Add keys"
        ref={keyInput}
      />
    </div>
  );
};

export default KeyInput;
