import React, { useEffect } from "react";
import { useSelector } from "react-redux";

import MainHeader from "./components/Header/MainHeader";
import FollowingsSection from "./components/Followings/FollowingsSection";
import TwitsSection from "./components/Twits/TwitsSection";
import KeySection from "./components/Keys/KeySection";
import { sendKeysData } from "./store/keys-actions";

let isInitial = true;

const App = () => {
  const keys = useSelector((state) => state.keys.keys);

  useEffect(() => {
    if (isInitial) {
      isInitial = false;
      return;
    }
    sendKeysData(keys);
  }, [keys]);

  return (
    <>
      <MainHeader />
      <main>
        <FollowingsSection />
        <TwitsSection />
        <KeySection />
      </main>
    </>
  );
};

export default App;
