import React from "react";

import classes from "./AddTwitter.module.css";
import SectionBottom from "../Layout/SectionBottom";
import useInput from "../../hooks/use-input";

const AddTwitter = () => {
  const {
    value: enteredHandleValue,
    hasError: handleInputHasError,
    isValid: handleInputIsValid,
    valueChangeHandler: handleInputChangeHandler,
    inputBlurHandler: handleInputBlurHandler,
    reset: handleInputReset,
  } = useInput((value) => value.trim().length >= 3 && value.includes("@"));

  const formSubmitHandler = (event) => {
    event.preventDefault();

    handleInputReset();
  };

  const handleInputClasses = !handleInputHasError
    ? "form-control"
    : "form-control invalid";

  let formIsValid = false;
  if (handleInputIsValid) {
    formIsValid = true;
  }

  return (
    <SectionBottom>
      <form onSubmit={formSubmitHandler} className={classes.add__followings}>
        <input
          type="text"
          placeholder="@twitterhandle"
          className={handleInputClasses}
          value={enteredHandleValue}
          onChange={handleInputChangeHandler}
          onBlur={handleInputBlurHandler}
        />
        {handleInputHasError && (
          <p className="error-text">Enter should have '@' symbol</p>
        )}
        <button className={classes.btn__add}>Add</button>
      </form>
    </SectionBottom>
  );
};

export default AddTwitter;
