import { useDispatch } from "react-redux";

export const Authenticate = (url, errorText = "Something went wrong...") => {
  // let url =
  //   "https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyBD3lPiWWjbfHBAvg0UlC2IOXOzKqlhSTY";

  const dispatch = useDispatch();

  const sendRequest = async () => {
    const response = await fetch(url, {
      method: "POST",
      body: JSON.stringify({
        email: enteredEmail,
        password: enteredPassword,
        returnSecureToken: true,
      }),
      headers: {
        "Content-Type": "application/json",
      },
    });

    if (!response.ok) {
      throw new Error(errorText);
    }

    const data = await response.json();
    dispatch(authActions.logIn(data.idToken));
  };

  sendRequest().catch((err) => {
    dispatch(authActions.showError(err.message));
  });

  document.activeElement.blur();
  resetEmailInput();
  resetPasswordInput();
};
